import{_ as l,c as e,o as i,a}from"./app.570737f8.js";const G=JSON.parse('{"title":"常用坐标系及EPSG --笔记","description":"","frontmatter":{},"headers":[{"level":2,"title":"一、常用坐标系","slug":"一、常用坐标系","link":"#一、常用坐标系","children":[]},{"level":2,"title":"二、企业常用坐标系","slug":"二、企业常用坐标系","link":"#二、企业常用坐标系","children":[]}],"relativePath":"GIS/常用坐标系及EPSG.md","lastUpdated":1669705325000}'),t={name:"GIS/常用坐标系及EPSG.md"},o=a('<h1 id="常用坐标系及epsg-笔记" tabindex="-1">常用坐标系及EPSG --笔记 <a class="header-anchor" href="#常用坐标系及epsg-笔记" aria-hidden="true">#</a></h1><blockquote><p>EPSG编号标志一个坐标系，包括地理坐标系 和 投影坐标系</p><p>官网：<a href="https://epsg.io/" target="_blank" rel="noreferrer">https://epsg.io/</a> <code>支持在线转换坐标</code></p></blockquote><h2 id="一、常用坐标系" tabindex="-1">一、常用坐标系 <a class="header-anchor" href="#一、常用坐标系" aria-hidden="true">#</a></h2><ul><li><p>4326</p><ul><li>WGS84地理坐标系</li></ul></li><li><p>3857</p><ul><li>WGS84墨卡托</li></ul></li><li><p>4490</p><ul><li>CGCS2000 地理坐标系 ，如天地图</li></ul></li></ul><h2 id="二、企业常用坐标系" tabindex="-1">二、企业常用坐标系 <a class="header-anchor" href="#二、企业常用坐标系" aria-hidden="true">#</a></h2><ol><li>GCJ-02 <ul><li>国测局02年发布的坐标体系，对经纬度数据的加密算法，加入了随机偏差</li><li>互联网地图在国内至少使用GCJ-02进行首次加密，高德、腾讯、Google中国地图 使用此坐标系 <ul><li>高德：<code>WGS-84——&gt;GCJ-02（高德有接口提供，反过来没有）</code></li><li>腾讯：<code>GCJ02</code></li><li>Google：<code>数据来源于高德，两者互通</code></li><li>阿里云：<code>GCJ02</code></li></ul></li></ul></li><li>BD-09 <ul><li>由GCJ-02进一步偏移得到</li><li>百度使用</li><li><code>支持WGS-84、GCJ-02转换成BD09，反向不支持，并且批量转换一次有条数限制</code></li></ul></li><li>CGCS2000 <ul><li>天地图</li></ul></li></ol>',6),d=[o];function r(c,s,_,n,h,p){return i(),e("div",null,d)}const S=l(t,[["render",r]]);export{G as __pageData,S as default};
