import{_ as t,o as d,c as a,Q as r}from"./chunks/framework.a7175731.js";const p=JSON.parse('{"title":"常见遥感影像  --笔记","description":"","frontmatter":{},"headers":[],"relativePath":"GIS/遥感/常见遥感影像.md","filePath":"GIS/遥感/常见遥感影像.md","lastUpdated":null}'),e={name:"GIS/遥感/常见遥感影像.md"},o=r('<h1 id="常见遥感影像-笔记" tabindex="-1">常见遥感影像 --笔记 <a class="header-anchor" href="#常见遥感影像-笔记" aria-label="Permalink to &quot;常见遥感影像  --笔记&quot;">​</a></h1><blockquote><p>地理空间数据云的常见影像及波段信息汇总</p></blockquote><h2 id="一、landsat系列" tabindex="-1">一、Landsat系列 <a class="header-anchor" href="#一、landsat系列" aria-label="Permalink to &quot;一、Landsat系列&quot;">​</a></h2><h4 id="_1-1-landsat8-oli-tirs" tabindex="-1">1.1 landsat8 OLI_TIRS <a class="header-anchor" href="#_1-1-landsat8-oli-tirs" aria-label="Permalink to &quot;1.1 landsat8 OLI_TIRS&quot;">​</a></h4><blockquote><p>时间：2013 年2月11日，美国航空航天局(NASA) 成功发射Landsat-8卫星</p><p>范围：成像宽幅为185x185km,</p><p>周期：每16 天可以实现一次全球覆盖</p></blockquote><h5 id="波段信息" tabindex="-1">波段信息 <a class="header-anchor" href="#波段信息" aria-label="Permalink to &quot;波段信息&quot;">​</a></h5><table><thead><tr><th>Landsat 8 OLI 陆地成像仪TIRS热红外传感器</th><th><strong>波段</strong></th><th><strong>波长（微米）</strong></th><th><strong>分辨率（米）</strong></th></tr></thead><tbody><tr><td>波段1-气溶胶</td><td>0.43-0.45</td><td>30</td><td></td></tr><tr><td>波段2-蓝</td><td>0.45-0.51</td><td>30</td><td></td></tr><tr><td>波段3-绿</td><td>0.53-0.59</td><td>30</td><td></td></tr><tr><td>波段4-红</td><td>0.64-0.67</td><td>30</td><td></td></tr><tr><td>波段5-近红</td><td>0.85-0.88</td><td>30</td><td></td></tr><tr><td>波段6-SWIR1</td><td>1.57-1.65</td><td>30</td><td></td></tr><tr><td>波段7-SWIR2</td><td>2.11-2.29</td><td>30</td><td></td></tr><tr><td>波段8-全色</td><td>0.50-0.68</td><td>15</td><td></td></tr><tr><td>波段9- Cirrus</td><td>1.36-1.38</td><td>30</td><td></td></tr><tr><td>波段10-TIRS热红外传感器1</td><td>10.60-11.19</td><td>100</td><td></td></tr><tr><td>波段11-TIRS热红外传感器2</td><td>11.50-12.51</td><td>100</td><td></td></tr></tbody></table><h5 id="产品参数" tabindex="-1">产品参数 <a class="header-anchor" href="#产品参数" aria-label="Permalink to &quot;产品参数&quot;">​</a></h5><table><thead><tr><th>产品类型</th><th>Level 1T地形矫正影像</th></tr></thead><tbody><tr><td>分辨率</td><td>1-7,9 -OLI多光谱波段（30米）；8 –OLI全色波段（15米）；10,11-TIRS波段（30米）</td></tr><tr><td>输出格式</td><td>GeoTIFF</td></tr><tr><td>取样方法</td><td>三次卷积算法（Cubic Convolution Resampling）</td></tr><tr><td>地图投影</td><td>UTM-WGS84投影坐标系</td></tr><tr><td>地形矫正</td><td>L1 数据产品已经经过系统辐射校正和几何校正</td></tr><tr><td>数据大小</td><td>约1GB （解压后约2GB）</td></tr></tbody></table><p>💡 三次卷积算法：遥感影像处理中的三次卷积算法通常是指<code>应用某个卷积核三次或者三层卷积核</code>来增强或提取图像中的特定特征。卷积运算是图像处理和计算机视觉中一种非常重要的操作，它通过将一个函数（在图像处理中通常是一个<code>矩阵或卷积核</code>）与图像进行组合的方式来改变图像的特性。</p><h4 id="_1-2-landsat7-etm-slc-off" tabindex="-1">1.2 Landsat7 ETM SLC-off <a class="header-anchor" href="#_1-2-landsat7-etm-slc-off" aria-label="Permalink to &quot;1.2 Landsat7 ETM SLC-off&quot;">​</a></h4><blockquote><p>时间：美国陆地卫星7 号(Landsat-7 ) 于1999 年4 月15 日由美国航空航天局(NASA) 发射升空</p><p>范围：南北的扫描范围大约为170km，东西的扫描范围大约为183km</p><p>周期：每16 天可以实现一次全球覆盖</p></blockquote><h5 id="波段信息-1" tabindex="-1">波段信息 <a class="header-anchor" href="#波段信息-1" aria-label="Permalink to &quot;波段信息&quot;">​</a></h5><table><thead><tr><th><strong>Band</strong></th><th><strong>波段</strong></th><th><strong>波长（微米）</strong></th><th><strong>分辨率（米）</strong></th><th><strong>主要作用</strong></th></tr></thead><tbody><tr><td>Band 1</td><td>蓝色波段</td><td>0.45-0.52</td><td>30</td><td>用于水体穿透,分辨土壤植被</td></tr><tr><td>Band 2</td><td>绿色波段</td><td>0.52-0.60</td><td>30</td><td>分辨植被</td></tr><tr><td>Band 3</td><td>红色波段</td><td>0.63-0.69</td><td>30</td><td>处于叶绿素吸收区域, 用于观测道路/裸露土壤/植被种类效果很好</td></tr><tr><td>Band 4</td><td>近红外</td><td>0.76-0.90</td><td>30</td><td>用于估算生物数量, 尽管这个波段可以从植被中区分出水体，分辨潮湿土壤，但是对于道路辨认效果不如TM3</td></tr><tr><td>Band 5</td><td>中红外</td><td>1.55-1.75</td><td>30</td><td>用于分辨道路/裸露土壤/水, 它还能在不同植被之间有好的对比度, 并且有较好的穿透大气、云雾的能力</td></tr><tr><td>Band 6</td><td>热红外</td><td>10.40-12.50</td><td>30</td><td>感应发出热辐射的目标</td></tr><tr><td>Band 7</td><td>中红外</td><td>2.09-2.35</td><td>30</td><td>对于岩石/矿物的分辨很有用, 也可用于辨识植被覆盖和湿润土壤</td></tr><tr><td>Band 8</td><td>微米全色</td><td>0.52-0.90</td><td>15</td><td>得到的是黑白图象, 分辨率为15m, 用于增强分辨率, 提供分辨能力</td></tr></tbody></table><h5 id="产品参数-1" tabindex="-1">产品参数 <a class="header-anchor" href="#产品参数-1" aria-label="Permalink to &quot;产品参数&quot;">​</a></h5>',15),n=[o];function s(h,l,i,c,_,b){return d(),a("div",null,n)}const f=t(e,[["render",s]]);export{p as __pageData,f as default};
