import{_ as e,o as a,c as t,Q as r}from"./chunks/framework.a7175731.js";const g=JSON.parse('{"title":"机器学习  --笔记","description":"","frontmatter":{},"headers":[],"relativePath":"个人/常识/机器学习.md","filePath":"个人/常识/机器学习.md","lastUpdated":null}'),n={name:"个人/常识/机器学习.md"},o=r('<h1 id="机器学习-笔记" tabindex="-1">机器学习 --笔记 <a class="header-anchor" href="#机器学习-笔记" aria-label="Permalink to &quot;机器学习  --笔记&quot;">​</a></h1><blockquote><p>机器学习的概念是指利用计算机算法和统计模型来使计算机系统具有学习能力的一门学科。</p></blockquote><h2 id="一、常见机器学习方法" tabindex="-1">一、常见机器学习方法 <a class="header-anchor" href="#一、常见机器学习方法" aria-label="Permalink to &quot;一、常见机器学习方法&quot;">​</a></h2><ol><li><strong>监督学习（Supervised Learning）：</strong> 通过给定标记的训练数据来训练模型，使其能够预测新数据的标记或值。</li><li><strong>无监督学习（Unsupervised Learning）：</strong> 使用未标记的数据来训练模型，发现数据之间的隐藏结构和模式。</li><li><strong>半监督学习（Semi-supervised Learning）：</strong> 同时使用标记和未标记的数据来训练模型，通常用于数据量较大但标记数据较少的情况。</li><li><strong>强化学习（Reinforcement Learning）：</strong> 通过与环境的交互来学习决策策略，以最大化累积奖励。</li><li><strong>深度学习（Deep Learning）：</strong> 一种基于人工神经网络的机器学习方法，用于处理大规模的高维数据，并在诸如图像识别、语音识别和自然语言处理等领域取得了显著的成果</li></ol><h2 id="二、" tabindex="-1">二、 <a class="header-anchor" href="#二、" aria-label="Permalink to &quot;二、&quot;">​</a></h2>',5),i=[o];function s(l,_,c,d,h,p){return a(),t("div",null,i)}const m=e(n,[["render",s]]);export{g as __pageData,m as default};
