import{_ as a,c as e,o as t,a as l}from"./app.56c30c72.js";const i="/assets/image-20221128200916617.9aabd464.png",r="/assets/image-20230226170229588.a6b045cb.png",b=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"2023Java基础","slug":"_2023java基础","link":"#_2023java基础","children":[]},{"level":2,"title":"一、概述","slug":"一、概述","link":"#一、概述","children":[]},{"level":2,"title":"二、安装","slug":"二、安装","link":"#二、安装","children":[{"level":3,"title":"1.安装步骤","slug":"_1-安装步骤","link":"#_1-安装步骤","children":[]},{"level":3,"title":"2.目录介绍","slug":"_2-目录介绍","link":"#_2-目录介绍","children":[]},{"level":3,"title":"3.环境变量","slug":"_3-环境变量","link":"#_3-环境变量","children":[]}]}],"relativePath":"后端/java/lecture.md","lastUpdated":1685614265000}'),d={name:"后端/java/lecture.md"},c=l('<h2 id="_2023java基础" tabindex="-1">2023Java基础 <a class="header-anchor" href="#_2023java基础" aria-hidden="true">#</a></h2><h2 id="一、概述" tabindex="-1">一、概述 <a class="header-anchor" href="#一、概述" aria-hidden="true">#</a></h2><p><strong>Java技术体系</strong></p><table><thead><tr><th>名称</th><th>介绍</th></tr></thead><tbody><tr><td>JavaSE （Java Standard Edition）标准版</td><td>支持面向桌面端应用的JAVA平台，旧版本叫做J2SE</td></tr><tr><td>JavaEE（Java Enterprise Edition）企业版</td><td>为开发企业环境下应用程序的一套及决方案，包含技术主要如Servlet、Jsp，主要针对Web应用程序开发，旧版本叫做J2EE</td></tr><tr><td>JavaME（Java Micro Editon）小型版</td><td>支持Java程序运行在移动终端上的平台，旧版本叫做J2ME</td></tr></tbody></table><blockquote><p>各类框架<code>Hadoop、Spark</code>等大数据开发平台也是Java编写成。</p></blockquote><blockquote><p>存储单位 1KB=1024B（byte）=1024 * 8bit</p></blockquote><blockquote><p>JDK = JRE + 开发工具；JRE = JVM + JAVA核心类库； 开发工具 = 编译工具javac.exe + 打包工具jar.exe</p><ul><li>JDK ：JAVA开发工具包</li><li>JRE： JAVA运行环境</li><li>JVM：JAVA虚拟机，通过JVM，实现同一个JAVA程序可在不同的操作系统执行</li></ul><p>使用JDK的开发工具完成JAVA程序，交给JRE运行</p></blockquote><p><img src="'+i+'" alt="image-20221128200916617"></p><h2 id="二、安装" tabindex="-1">二、安装 <a class="header-anchor" href="#二、安装" aria-hidden="true">#</a></h2><h3 id="_1-安装步骤" tabindex="-1">1.安装步骤 <a class="header-anchor" href="#_1-安装步骤" aria-hidden="true">#</a></h3><p>略</p><h3 id="_2-目录介绍" tabindex="-1">2.目录介绍 <a class="header-anchor" href="#_2-目录介绍" aria-hidden="true">#</a></h3><ul><li>bin : 存放开发工具 <ul><li>javac.exe 编译</li><li>java.exe 运行</li><li>javadoc.exe 生成网页文档</li></ul></li><li>include： java调用C</li><li>jre：运行时环境</li><li>lib：jar包</li><li>src：常见开源代码</li></ul><h3 id="_3-环境变量" tabindex="-1">3.环境变量 <a class="header-anchor" href="#_3-环境变量" aria-hidden="true">#</a></h3><p>path：Windows系统执行命令要搜寻的路径</p><p><code>按照需求更换环境变量中的JAVA_HOME值</code></p><p>4.开发三步骤</p><ul><li>将Java代码编写到扩展名.java源文件中</li><li>通过javac.exe命令对java文件编译，生成一个或多个字节码文件</li><li>通过java.exe命令对生成的class文件运行</li></ul><p><img src="'+r+'" alt="image-20230226170229588"></p><p>P14</p>',20),n=[c];function o(s,h,_,p,v,u){return t(),e("div",null,n)}const A=a(d,[["render",o]]);export{b as __pageData,A as default};
