import{_ as i,c as a,a2 as e,o as r}from"./chunks/framework.BQmytedh.js";const p=JSON.parse('{"title":"CorelDraw","description":"","frontmatter":{},"headers":[],"relativePath":"工具/CorelDraw.md","filePath":"工具/CorelDraw.md","lastUpdated":1767866075000}'),u={name:"工具/CorelDraw.md"};function t(o,l,h,n,d,s){return r(),a("div",null,l[0]||(l[0]=[e('<h1 id="coreldraw" tabindex="-1">CorelDraw <a class="header-anchor" href="#coreldraw" aria-label="Permalink to &quot;CorelDraw&quot;">​</a></h1><h2 id="一、界面" tabindex="-1">一、界面 <a class="header-anchor" href="#一、界面" aria-label="Permalink to &quot;一、界面&quot;">​</a></h2><ul><li>菜单栏</li><li>功能栏</li><li>工具栏</li><li>工具组 <ul><li>不同工具的属性栏</li></ul></li><li>文件标题栏 <ul><li>一个文件中可有多页</li></ul></li><li>工作区</li><li>状态栏</li></ul><h2 id="二、基础操作" tabindex="-1">二、基础操作 <a class="header-anchor" href="#二、基础操作" aria-label="Permalink to &quot;二、基础操作&quot;">​</a></h2><h4 id="界面操作" tabindex="-1">界面操作 <a class="header-anchor" href="#界面操作" aria-label="Permalink to &quot;界面操作&quot;">​</a></h4><ul><li>放大缩小 <ul><li>shift按键可以缩小</li><li>滚轮键向前放大</li><li>滚轮键向后缩小</li></ul></li><li>平移 <ul><li>按住滚轮键</li></ul></li><li>颜色 <ul><li>填充颜色左键填充，右键是边框颜色更改</li></ul></li></ul><h4 id="对象选择" tabindex="-1">对象选择 <a class="header-anchor" href="#对象选择" aria-label="Permalink to &quot;对象选择&quot;">​</a></h4><ul><li><p>取消所有对象组合</p></li><li><p>手绘选择</p><ul><li>alt键再选中对象的一点就可选中全部</li></ul></li><li><p>形状工具</p><ul><li>锚点更改位置</li></ul></li></ul><h4 id="其他操作" tabindex="-1">其他操作 <a class="header-anchor" href="#其他操作" aria-label="Permalink to &quot;其他操作&quot;">​</a></h4><ul><li>复制 ctrl C/V</li><li>填充 <ul><li>鼠标左键右侧的调色板</li><li>鼠标右键右侧的调色板 <ul><li>下方状态栏有更为详尽的颜色选择方案</li></ul></li></ul></li></ul><h2 id="三、文本工具" tabindex="-1">三、文本工具 <a class="header-anchor" href="#三、文本工具" aria-label="Permalink to &quot;三、文本工具&quot;">​</a></h2><ul><li>工具组中的字 <ul><li>直接单击：美术字 <ul><li>工具属性栏 <ul><li>XY : 所在位置</li><li>放大缩小字体</li></ul></li><li>单击中间十字架——切换旋转和放大缩小</li><li>水平 / 竖直 翻转</li><li>美术字 和 段落文本的对齐方式</li><li>文本属性 <ul><li>内部有多项属性功能</li></ul></li><li>其他：文本转曲，可以实现矢栅之间转换</li></ul></li><li>拉选框：段落文本 <ul><li>工具属性栏</li><li>文本属性</li></ul></li></ul></li><li>工具组中地路径文本 <ul><li>画好路径后--文本选中对应周边轮廓即可</li></ul></li></ul><h2 id="四、制表符" tabindex="-1">四、制表符 <a class="header-anchor" href="#四、制表符" aria-label="Permalink to &quot;四、制表符&quot;">​</a></h2><ul><li>可单行 单列 多行 多列更改大小</li><li>制表符可通过调整水平、垂直来设置居中</li><li>制表符的外边框、内边框或者单一行等可调整边框粗细</li><li>通过顶部菜单栏 --表格，可将文字转换为表格</li></ul><h2 id="五、绘图工具" tabindex="-1">五、绘图工具 <a class="header-anchor" href="#五、绘图工具" aria-label="Permalink to &quot;五、绘图工具&quot;">​</a></h2><ul><li>手绘工具可调整线宽、线形状等</li><li>两点线可绘制对应切线、垂直线</li><li>三点曲线、贝塞尔曲线等</li><li>钢笔工具，按住后移动可调整曲度 + 形状工具，可以调整节点所在位置及弯曲度</li></ul><p>补：转换为曲线后就可以对节点进行更改设置</p><h2 id="六、矩形工具" tabindex="-1">六、矩形工具 <a class="header-anchor" href="#六、矩形工具" aria-label="Permalink to &quot;六、矩形工具&quot;">​</a></h2><ul><li>随便画出矩形</li><li>如果需要正方形，需要按住Ctrl</li><li>椭圆，如果需要画标准圆，按住Ctrl</li><li>其他形状工具</li></ul><h2 id="七、裁剪工具组" tabindex="-1">七、裁剪工具组 <a class="header-anchor" href="#七、裁剪工具组" aria-label="Permalink to &quot;七、裁剪工具组&quot;">​</a></h2><ul><li><p>裁剪工具，可裁剪出需要的图片区域。</p></li><li><p>刻刀工具，可将图片裁剪成两部分，分别用</p></li><li><p>虚拟段删除，可以删除内部的连接线</p><ul><li>内部删除完毕后，可以将外部轮廓线通过顶部的焊接工具将边框焊接为一个整体</li></ul></li><li><p>橡皮擦工具，可以对转曲后的图形擦除工作</p></li></ul><h2 id="八、对象编辑" tabindex="-1">八、对象编辑 <a class="header-anchor" href="#八、对象编辑" aria-label="Permalink to &quot;八、对象编辑&quot;">​</a></h2><ul><li>泊坞窗 <ul><li>变换：位置、旋转、缩放和镜像、大小和倾斜 <ul><li>注意副本的数目是否为0</li></ul></li><li>对齐与分布 <ul><li>用于调整对象之间的对齐方式和中间的间隔</li></ul></li></ul></li><li>对象造型工具（同ArcMap空间分析工具箱） <ul><li>焊接</li><li>修建</li><li>相交</li><li>移除前方/后方对象</li><li>创建边界</li></ul></li></ul><h2 id="九、阴影工具" tabindex="-1">九、阴影工具 <a class="header-anchor" href="#九、阴影工具" aria-label="Permalink to &quot;九、阴影工具&quot;">​</a></h2><ul><li>阴影：可调整阴影的方向、程度以及羽化程度（模糊程度）</li><li>轮廓图 <ul><li>向内或向外增加轮廓</li><li>更改轮廓数目、各轮廓间的宽度、顶角样式等</li></ul></li><li>混合 <ul><li>显示出两个图形之间的相互转化过程</li></ul></li><li>变形和封套，可作出很多不规则图形，如旋转手里剑</li><li>立体化 <ul><li>可将某一几何对象改为立体图形</li></ul></li><li>块阴影 <ul><li>可朝某个方向拉出一片阴影区域</li></ul></li></ul><h2 id="十、度量工具" tabindex="-1">十、度量工具 <a class="header-anchor" href="#十、度量工具" aria-label="Permalink to &quot;十、度量工具&quot;">​</a></h2><ul><li>水平、垂直或倾斜度量</li><li>线段度量</li><li>其他标注</li><li>锚点工具</li></ul>',27)]))}const b=i(u,[["render",t]]);export{p as __pageData,b as default};
