import{_ as a,o as e,c as l,Q as i}from"./chunks/framework.a7175731.js";const t="/assets/image-20231005111336590.10db8cb1.png",r="/assets/image-20231003165132091.3e3f15ca.png",o="/assets/image-20231003171137717.b61bef08.png",n="/assets/image-20231003171315182.192489ea.png",h="/assets/image-20231003171324079.d9bf9c22.png",k=JSON.parse('{"title":"常见坐标系 --笔记","description":"","frontmatter":{},"headers":[],"relativePath":"数学/一、常见坐标系.md","filePath":"数学/一、常见坐标系.md","lastUpdated":null}'),s={name:"数学/一、常见坐标系.md"},c=i('<h1 id="常见坐标系-笔记" tabindex="-1">常见坐标系 --笔记 <a class="header-anchor" href="#常见坐标系-笔记" aria-label="Permalink to &quot;常见坐标系 --笔记&quot;">​</a></h1><h2 id="一、为什么需要多个坐标空间" tabindex="-1">一、为什么需要多个坐标空间 <a class="header-anchor" href="#一、为什么需要多个坐标空间" aria-label="Permalink to &quot;一、为什么需要多个坐标空间&quot;">​</a></h2><h5 id="原因" tabindex="-1">原因 <a class="header-anchor" href="#原因" aria-label="Permalink to &quot;原因&quot;">​</a></h5><ul><li>某些信息仅在特定坐标系中是已知的;</li><li>例如一个点 a, 我们可能不知道其在世界坐标系中的位置;</li><li>但我们或许表达 a 相对于其他坐标系的位置;</li></ul><h2 id="二、常见坐标空间" tabindex="-1">二、常见坐标空间 <a class="header-anchor" href="#二、常见坐标空间" aria-label="Permalink to &quot;二、常见坐标空间&quot;">​</a></h2><h5 id="世界空间-全局坐标空间-通用坐标空间" tabindex="-1">世界空间(全局坐标空间/通用坐标空间) <a class="header-anchor" href="#世界空间-全局坐标空间-通用坐标空间" aria-label="Permalink to &quot;世界空间(全局坐标空间/通用坐标空间)&quot;">​</a></h5><ul><li>坐标: 球面坐标;</li><li>原点: [0, 0];</li></ul><h5 id="对象空间-体空间" tabindex="-1">对象空间(体空间) <a class="header-anchor" href="#对象空间-体空间" aria-label="Permalink to &quot;对象空间(体空间)&quot;">​</a></h5><ul><li>与特定对象关联的坐标空间;</li><li>每个对象都有自己独立的对象空间;</li></ul><h5 id="相机空间" tabindex="-1">相机空间 <a class="header-anchor" href="#相机空间" aria-label="Permalink to &quot;相机空间&quot;">​</a></h5><ul><li>与用于渲染的视点相关联的对象空间;</li><li>使用左手坐标系;</li><li>原点为相机;</li></ul><h5 id="直立空间" tabindex="-1">直立空间 <a class="header-anchor" href="#直立空间" aria-label="Permalink to &quot;直立空间&quot;">​</a></h5><ul><li>世界空间和对象空间转换的中间坐标空间;</li><li><code>直立空间轴线与世界空间平行;</code></li><li><code>直立空间原点和对象空间原点重合;</code></li></ul><h5 id="直立空间的转换" tabindex="-1">直立空间的转换 <a class="header-anchor" href="#直立空间的转换" aria-label="Permalink to &quot;直立空间的转换&quot;">​</a></h5><ul><li>对象空间 - 直立空间: 旋转;</li><li>直立空间 - 世界空间: 平移;</li></ul><p><img src="'+t+'" alt="image-20231005111336590"></p><h2 id="三、基向量" tabindex="-1">三、基向量 <a class="header-anchor" href="#三、基向量" aria-label="Permalink to &quot;三、基向量&quot;">​</a></h2><h5 id="基向量" tabindex="-1">基向量 <a class="header-anchor" href="#基向量" aria-label="Permalink to &quot;基向量&quot;">​</a></h5><ul><li>p, q, r 是三维空间的基向量;</li><li>v 为基向量的线性组合;</li><li>世界空间的基向量一般为 [1, 0, 0], [0, 1, 0], [0, 0, 1]</li></ul><p>$$v=xp+yq+zr$$</p><h5 id="良好的基向量" tabindex="-1">良好的基向量 <a class="header-anchor" href="#良好的基向量" aria-label="Permalink to &quot;良好的基向量&quot;">​</a></h5><ul><li>一般选择相互垂直的基向量;</li><li>Span(p, q, r) 线性无关;</li></ul><blockquote><p>补充</p><ol><li>Span(p, q, r) 表示由向量 p、q、r 线性组合生成的子空间。这个子空间包括了所有可以通过对 p、q、r 进行线性组合（加法和标量乘法）得到的向量</li><li><strong>线性相关（Linear Dependence）</strong>： 如果一组向量中的某个向量可以表示为其他向量的线性组合，或者说存在一组不全为零的标量（系数），使得这些向量的线性组合等于零向量，那么这组向量被称为线性相关的。</li><li><strong>线性无关（Linear Independence）</strong> ： 如果一组向量中的每个向量不能表示为其他向量的线性组合，并且不存在一组非零标量，使得这些向量的线性组合等于零向量，那么这组向量被称为线性无关的。</li></ol></blockquote><h5 id="正交基" tabindex="-1">正交基 <a class="header-anchor" href="#正交基" aria-label="Permalink to &quot;正交基&quot;">​</a></h5><ul><li>相互垂直的基向量;</li></ul><h5 id="标准正交基" tabindex="-1">标准正交基 <a class="header-anchor" href="#标准正交基" aria-label="Permalink to &quot;标准正交基&quot;">​</a></h5><ul><li>单位长度的正交基;</li></ul><h2 id="四、2维笛卡尔坐标系" tabindex="-1">四、2维笛卡尔坐标系 <a class="header-anchor" href="#四、2维笛卡尔坐标系" aria-label="Permalink to &quot;四、2维笛卡尔坐标系&quot;">​</a></h2><blockquote><p>Cesium中类名为 “Cartesian2”</p></blockquote><h3 id="_1-1-概念" tabindex="-1">1.1 概念 <a class="header-anchor" href="#_1-1-概念" aria-label="Permalink to &quot;1.1 概念&quot;">​</a></h3><p>一种用于表示平面上点位置的数学坐标系统。</p><p><img src="'+r+'" alt="image-20231003165132091"></p><p>💡 与常见的直角坐标系相比，二维笛卡尔坐标系没有明显的区别，因为它们实际上是同一概念的不同名称。</p><p>💡 笛卡尔坐标系 与 仿射坐标系的区别</p><ul><li>仿射坐标系是一种更一般化的坐标系统，用于描述平面或多维空间中的点位置 <ul><li>在仿射坐标系中，不仅可以使用直角坐标系的x和y坐标来表示点的位置，还可以使用其他坐标系统来描述点的位置，例如极坐标、球坐标等</li></ul></li><li>仿射坐标系还可以包括平移、旋转和缩放等仿射变换，这些变换可以改变点在坐标系中的位置和方向，而笛卡尔坐标系通常不包括这些变换。</li></ul><h2 id="五、3维笛卡尔坐标系" tabindex="-1">五、3维笛卡尔坐标系 <a class="header-anchor" href="#五、3维笛卡尔坐标系" aria-label="Permalink to &quot;五、3维笛卡尔坐标系&quot;">​</a></h2><blockquote><p>Cesium中类名为 “Cartesian3”</p></blockquote><h3 id="_2-1-概念" tabindex="-1">2.1 概念 <a class="header-anchor" href="#_2-1-概念" aria-label="Permalink to &quot;2.1 概念&quot;">​</a></h3><p>三维笛卡尔坐标系是一种用于表示三维空间中点位置的数学坐标系统。它是二维笛卡尔坐标系的扩展，通过引入第三个坐标轴，通常称为z轴，来描述三维空间中的点。在三维笛卡尔坐标系中，一个点的位置由三个坐标值 (x, y, z) 来确定，其中x表示点在水平方向的位置，y表示点在垂直方向的位置，z表示点在竖直方向的位置。</p><p><img src="'+o+'" alt="image-20231003171137717"></p><p>📖 左手坐标系 和 右手坐标系</p><p>让拇指和食指成“L”形，大拇指向右，食指向上。其余的手指指向前方，拇指、食指和其余三个手指分别代表x、y、z轴的正方向。</p><p><img src="'+n+'" alt="image-20231003171315182"></p><p>同样，伸出右手，使食指向上，其余三指向前，拇指这时指向左，这就是一个右手坐标系，拇指、食指和其余三个手指分别代表x,y,z轴的正方向。</p><p><img src="'+h+'" alt="image-20231003171324079"></p>',45),d=[c];function u(p,m,q,_,b,f){return e(),l("div",null,d)}const g=a(s,[["render",u]]);export{k as __pageData,g as default};
