import{_ as a,o as e,c as i,Q as l}from"./chunks/framework.a7175731.js";const t="/assets/image-20231007094314052.9761b606.png",r="/assets/image-20231007094326304.1b3fe84e.png",o="/assets/image-20231007094635392.ecec42cd.png",h="/assets/image-20231007094804248.9b5c350e.png",n="/assets/image-20231007095548050.aa60df03.png",x=JSON.parse('{"title":"矩阵 --笔记","description":"","frontmatter":{},"headers":[],"relativePath":"数学/三、矩阵.md","filePath":"数学/三、矩阵.md","lastUpdated":null}'),s={name:"数学/三、矩阵.md"},m=l('<h1 id="矩阵-笔记" tabindex="-1">矩阵 --笔记 <a class="header-anchor" href="#矩阵-笔记" aria-label="Permalink to &quot;矩阵 --笔记&quot;">​</a></h1><h2 id="一、矩阵的数学定义" tabindex="-1">一、矩阵的数学定义 <a class="header-anchor" href="#一、矩阵的数学定义" aria-label="Permalink to &quot;一、矩阵的数学定义&quot;">​</a></h2><h3 id="_1-1-矩阵维度" tabindex="-1">1.1 矩阵维度 <a class="header-anchor" href="#_1-1-矩阵维度" aria-label="Permalink to &quot;1.1 矩阵维度&quot;">​</a></h3><ul><li>具有 r 行 c 列的矩阵;</li><li>称作 $r \\times c$;</li></ul><h3 id="_1-2-表示法" tabindex="-1">1.2 表示法 <a class="header-anchor" href="#_1-2-表示法" aria-label="Permalink to &quot;1.2 表示法&quot;">​</a></h3><ul><li>下标表示法;</li></ul><p>$$ \\begin{bmatrix} m_{11} &amp; m_{12} &amp; m_{13} \\ m_{21} &amp; m_{22} &amp; m_{23} \\ m_{31} &amp; m_{32} &amp; m_{33} \\end{bmatrix} $$</p><h3 id="_1-3-方形矩阵" tabindex="-1">1.3 方形矩阵 <a class="header-anchor" href="#_1-3-方形矩阵" aria-label="Permalink to &quot;1.3 方形矩阵&quot;">​</a></h3><h4 id="方形矩阵" tabindex="-1">方形矩阵 <a class="header-anchor" href="#方形矩阵" aria-label="Permalink to &quot;方形矩阵&quot;">​</a></h4><ul><li>具有相同行数和列数的矩阵;</li></ul><p>对角元素</p><ul><li>行和列索引相同的元素;</li></ul><p>对角矩阵D</p><ul><li>非对角元素均为 0;</li></ul><p>$$ \\begin{bmatrix} 1 &amp; 0 &amp; 0 \\ 0 &amp; 0 &amp; 0 \\ 0 &amp; 0 &amp; 3 \\end{bmatrix} $$</p><h4 id="单位矩阵" tabindex="-1">单位矩阵 <a class="header-anchor" href="#单位矩阵" aria-label="Permalink to &quot;单位矩阵&quot;">​</a></h4><ul><li>对角元素为 1, 其余元素为 0, 称作 I;</li></ul><p>$$ \\begin{bmatrix} 1 &amp; 0 &amp; 0 \\ 0 &amp; 1 &amp; 0 \\ 0 &amp; 0 &amp; 1 \\end{bmatrix} $$</p><h3 id="_1-4-作为矩阵的向量" tabindex="-1">1.4 作为矩阵的向量 <a class="header-anchor" href="#_1-4-作为矩阵的向量" aria-label="Permalink to &quot;1.4 作为矩阵的向量&quot;">​</a></h3><h4 id="行向量和列向量" tabindex="-1">行向量和列向量 <a class="header-anchor" href="#行向量和列向量" aria-label="Permalink to &quot;行向量和列向量&quot;">​</a></h4><ul><li>行向量: $1 \\times n$;</li><li>列向量: $n \\times 1$;</li></ul><p><img src="'+t+'" alt="image-20231007094314052"></p><h3 id="_1-5-矩阵转置" tabindex="-1">1.5 矩阵转置 <a class="header-anchor" href="#_1-5-矩阵转置" aria-label="Permalink to &quot;1.5 矩阵转置&quot;">​</a></h3><h4 id="矩阵转置" tabindex="-1">矩阵转置 <a class="header-anchor" href="#矩阵转置" aria-label="Permalink to &quot;矩阵转置&quot;">​</a></h4><ul><li>$r \\times c$ 矩阵 M;</li><li>其转置矩阵 $c \\times r, \\quad M^T$ 即 M 行列翻转;</li></ul><p><img src="'+r+'" alt="image-20231007094326304"></p><h4 id="性质" tabindex="-1">性质 <a class="header-anchor" href="#性质" aria-label="Permalink to &quot;性质&quot;">​</a></h4><ul><li>$(M^T)^T=M$;</li><li>对角矩阵 D, $D^T=D$;</li></ul><h3 id="_1-6-矩阵和标量相乘" tabindex="-1">1.6 矩阵和标量相乘 <a class="header-anchor" href="#_1-6-矩阵和标量相乘" aria-label="Permalink to &quot;1.6 矩阵和标量相乘&quot;">​</a></h3><h4 id="矩阵和标量相乘" tabindex="-1">矩阵和标量相乘 <a class="header-anchor" href="#矩阵和标量相乘" aria-label="Permalink to &quot;矩阵和标量相乘&quot;">​</a></h4><p><img src="'+o+'" alt="image-20231007094635392"></p><h3 id="_1-7-矩阵相乘" tabindex="-1">1.7 矩阵相乘 <a class="header-anchor" href="#_1-7-矩阵相乘" aria-label="Permalink to &quot;1.7 矩阵相乘&quot;">​</a></h3><h4 id="矩阵相乘" tabindex="-1">矩阵相乘 <a class="header-anchor" href="#矩阵相乘" aria-label="Permalink to &quot;矩阵相乘&quot;">​</a></h4><ul><li>$r \\times n$ 矩阵 A 可与 $n \\times c$ 矩阵 B 相乘;</li><li>结果为 $r \\times c$ 矩阵 C;</li></ul><p><img src="'+h+'" alt="image-20231007094804248"></p><h4 id="计算公式" tabindex="-1">计算公式 <a class="header-anchor" href="#计算公式" aria-label="Permalink to &quot;计算公式&quot;">​</a></h4><ul><li>对于 C 中的第 i 行, 第 j 列的元素;</li><li>对应 A 中的第 i 行, B 中的第 j 列;</li><li>A 和 B 的行和列的对应元素相乘求和;</li></ul><p>$$c_{ij} = \\sum^n_{k=1}a_{ik}b_{kj}$$</p><h4 id="性质-1" tabindex="-1">性质 <a class="header-anchor" href="#性质-1" aria-label="Permalink to &quot;性质&quot;">​</a></h4><ul><li>MI = IM = M;</li><li>$AB \\neq BA$;</li><li>(AB)C = A(BC);</li><li>(kA)B = k(AB) = A(kB);</li><li>$(AB)^T = B^TA^T$</li></ul><h3 id="_1-8-向量和矩阵相乘" tabindex="-1">1.8 向量和矩阵相乘 <a class="header-anchor" href="#_1-8-向量和矩阵相乘" aria-label="Permalink to &quot;1.8 向量和矩阵相乘&quot;">​</a></h3><h4 id="向量和矩阵相乘" tabindex="-1">向量和矩阵相乘 <a class="header-anchor" href="#向量和矩阵相乘" aria-label="Permalink to &quot;向量和矩阵相乘&quot;">​</a></h4><p><img src="'+n+'" alt="image-20231007095548050"></p><h2 id="二、矩阵的几何解释" tabindex="-1">二、矩阵的几何解释 <a class="header-anchor" href="#二、矩阵的几何解释" aria-label="Permalink to &quot;二、矩阵的几何解释&quot;">​</a></h2><ul><li>矩阵表示空间坐标变换;</li><li>矩阵的行可解释为坐标空间的基向量;</li><li>向量乘矩阵从一个坐标空间变换为另一个坐标空间; <ul><li>对于标准基向量, 标准基向量乘矩阵即经过矩阵变换后的坐标空间的基向量;</li></ul></li><li>p, q, r 表示一组基向量;</li></ul><h2 id="三、矩阵变换" tabindex="-1">三、矩阵变换 <a class="header-anchor" href="#三、矩阵变换" aria-label="Permalink to &quot;三、矩阵变换&quot;">​</a></h2><h3 id="旋转" tabindex="-1">旋转 <a class="header-anchor" href="#旋转" aria-label="Permalink to &quot;旋转&quot;">​</a></h3>',47),d=[m];function c(u,_,p,b,q,$){return e(),i("div",null,d)}const k=a(s,[["render",c]]);export{x as __pageData,k as default};
