import{_ as e,c as t,o as i,a}from"./app.08de8fd9.js";const l="/assets/image-20230624101250114.9d09e603.png",r="/assets/image-20230624102223267.867fbb30.png",d="/assets/image-20230624102235372.537c7fc2.png",o="/assets/image-20230624102308724.b828852a.png",s="/assets/image-20230624102327052.783dbbb0.png",c="/assets/image-20230624102349610.44abb50f.png",n="/assets/image-20230624102518208.a7aef8d8.png",p="/assets/image-20230624102543315.41c7e80e.png",h="/assets/image-20230624102552947.323257f6.png",b="/assets/image-20230624102618610.f7919062.png",u="/assets/image-20230624102654597.c55ffa3b.png",g="/assets/image-20230624102816890.cb1ee994.png",L=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"3DCityDB --笔记","slug":"_3dcitydb-笔记","link":"#_3dcitydb-笔记","children":[{"level":3,"title":"1、Home","slug":"_1、home","link":"#_1、home","children":[]},{"level":3,"title":"2、Privacy & Terms","slug":"_2、privacy-terms","link":"#_2、privacy-terms","children":[{"level":4,"title":"隐私条款","slug":"隐私条款","link":"#隐私条款","children":[]},{"level":4,"title":"网页检索","slug":"网页检索","link":"#网页检索","children":[]}]},{"level":3,"title":"3、Connect","slug":"_3、connect","link":"#_3、connect","children":[]},{"level":3,"title":"4、News","slug":"_4、news","link":"#_4、news","children":[]},{"level":3,"title":"5、Features","slug":"_5、features","link":"#_5、features","children":[{"level":4,"title":"3D City Database","slug":"_3d-city-database","link":"#_3d-city-database","children":[]},{"level":4,"title":"3D City Database Importer/Exporter","slug":"_3d-city-database-importer-exporter","link":"#_3d-city-database-importer-exporter","children":[]},{"level":4,"title":"3DCityDB-Web-Map-Client","slug":"_3dcitydb-web-map-client","link":"#_3dcitydb-web-map-client","children":[]}]},{"level":3,"title":"6、Demos","slug":"_6、demos","link":"#_6、demos","children":[]},{"level":3,"title":"7、Software","slug":"_7、software","link":"#_7、software","children":[{"level":4,"title":"3D City Database","slug":"_3d-city-database-1","link":"#_3d-city-database-1","children":[]},{"level":4,"title":"Importer/Exporter","slug":"importer-exporter","link":"#importer-exporter","children":[]},{"level":4,"title":"Importer/Exporter Plugins","slug":"importer-exporter-plugins","link":"#importer-exporter-plugins","children":[]},{"level":4,"title":"Web Feature Service","slug":"web-feature-service","link":"#web-feature-service","children":[]},{"level":4,"title":"3DCityDB-Web-Map-Client","slug":"_3dcitydb-web-map-client-1","link":"#_3dcitydb-web-map-client-1","children":[]},{"level":4,"title":"citygml4j","slug":"citygml4j","link":"#citygml4j","children":[]}]},{"level":3,"title":"8、Downloads","slug":"_8、downloads","link":"#_8、downloads","children":[]},{"level":3,"title":"9、Partners","slug":"_9、partners","link":"#_9、partners","children":[]},{"level":3,"title":"10、3DCityDB in Action","slug":"_10、3dcitydb-in-action","link":"#_10、3dcitydb-in-action","children":[]},{"level":3,"title":"11、Publications","slug":"_11、publications","link":"#_11、publications","children":[]}]}],"relativePath":"数据库/3DCityDB简介.md","lastUpdated":null}'),m={name:"数据库/3DCityDB简介.md"},_=a('<h2 id="_3dcitydb-笔记" tabindex="-1">3DCityDB --笔记 <a class="header-anchor" href="#_3dcitydb-笔记" aria-hidden="true">#</a></h2><p>官网链接：<a href="https://www.3dcitydb.org/" target="_blank" rel="noreferrer">https://www.3dcitydb.org/</a></p><h3 id="_1、home" tabindex="-1">1、Home <a class="header-anchor" href="#_1、home" aria-hidden="true">#</a></h3><blockquote><p>整个官网首页，包含对其他部分如DEMO、FEATURES 和 Partners 等的简要介绍，这里不是重点，略。</p></blockquote><h3 id="_2、privacy-terms" tabindex="-1">2、Privacy &amp; Terms <a class="header-anchor" href="#_2、privacy-terms" aria-hidden="true">#</a></h3><blockquote><p>网站的一些隐私条款、权利声明</p></blockquote><h4 id="隐私条款" tabindex="-1">隐私条款 <a class="header-anchor" href="#隐私条款" aria-hidden="true">#</a></h4><p>该网站的网络服务器由慕尼黑工业大学地理信息学教研室运营，地址为Arcisstraße 21, 80333 Munich。所处理的个人数据符合适用的数据保护条例，特别是《巴伐利亚数据保护法》（BayDSG）和《电信媒体法》（TMG）。</p><h4 id="网页检索" tabindex="-1">网页检索 <a class="header-anchor" href="#网页检索" aria-hidden="true">#</a></h4><p>每次访问我们网站的页面时，我们的网络服务器都会在日志文件中暂时存储以下信息：</p><ul><li>提出请求的计算机的IP地址</li><li>访问的日期和时间</li><li>检索文件的名称、URL和传输的数据量</li><li>访问状态（请求的文件已转移，未找到，等等）</li><li>所使用的浏览器和操作系统的识别数据（如果由请求的网络浏览器传输）。</li><li>访问的网站（如果由请求的网络浏览器传输）。</li></ul><p>该日志文件中的数据被处理如下：</p><ul><li>为了发现对网络服务器的攻击，并能作出相应的反应，日志条目会被持续自动评估。</li><li>在个别情况下，即在报告故障、错误和安全事件的情况下，会进行人工分析。</li><li>超过七天的日志条目会通过缩短IP地址进行匿名处理。</li><li>匿名的日志被用来创建访问统计数据。用于此的软件由LRZ在本地操作。</li></ul><p>LRZ不会将日志条目中包含的IP地址与其他数据库合并，因此LRZ不能得出任何关于个人的结论。</p><h3 id="_3、connect" tabindex="-1">3、Connect <a class="header-anchor" href="#_3、connect" aria-hidden="true">#</a></h3><blockquote><p>该网站建设者的联系方式，略。</p></blockquote><p><a href="https://www.3dcitydb.org/3dcitydb/contact/" target="_blank" rel="noreferrer">https://www.3dcitydb.org/3dcitydb/contact/</a></p><h3 id="_4、news" tabindex="-1">4、News <a class="header-anchor" href="#_4、news" aria-hidden="true">#</a></h3><blockquote><p>3DCityDB数据库的相关版本更新声明，不是重点，略。</p></blockquote><h3 id="_5、features" tabindex="-1">5、Features <a class="header-anchor" href="#_5、features" aria-hidden="true">#</a></h3><blockquote><p>3DCityDB数据库的相关特性和功能</p></blockquote><h4 id="_3d-city-database" tabindex="-1">3D City Database <a class="header-anchor" href="#_3d-city-database" aria-hidden="true">#</a></h4><blockquote><p>语义化的三维城市模型的空间关系数据库模式</p></blockquote><ul><li>包括CityGML 2.0的所有主题模块</li><li>五个<code>不同的细节级别（LODs）</code>。</li><li>外观数据（如<code>纹理、颜色</code>）</li><li><code>灵活的三维几何形状</code></li><li>支持通用和原型的三维物体</li><li>复杂的数字地形模型（DTMs）</li><li>网络特征服务（WFS）接口</li><li>与Oracle Spatial 10g、11g和12c、19c（Spatial和Locator）或<code>PostGIS 2.0或更高版本一起工作</code></li><li>可使用<code>Docker</code>容器进行快速设置和云操作</li></ul><h4 id="_3d-city-database-importer-exporter" tabindex="-1">3D City Database Importer/Exporter <a class="header-anchor" href="#_3d-city-database-importer-exporter" aria-hidden="true">#</a></h4><blockquote><p>导入/导出器</p></blockquote><ul><li>完全支持CityGML 2.0和1.0数据集的导入/导出；支持平铺式导入/导出</li><li>支持导入/导出CityJSON数据集</li><li>导出KML/COLLADA/glTF模型，以用于GIS和数字地球浏览器，如ArcGIS、CesiumJS、谷歌地球或iTowns。</li><li>通用的KML信息气球</li><li>读/写任意文件大小的CityGML文件；支持gzip和zip压缩</li><li>强大的查询和过滤功能</li><li>多线程编程促进高性能处理</li><li>解决前向和后向XL链接的问题</li><li>对CityGML文件进行XML验证</li><li>用户定义的坐标参考系统</li><li>用于CityGML输出的坐标转换</li><li>将专题数据导出到电子表格（CSV和Microsoft Excel）。</li><li>批量模式和图形用户界面</li></ul><h4 id="_3dcitydb-web-map-client" tabindex="-1">3DCityDB-Web-Map-Client <a class="header-anchor" href="#_3dcitydb-web-map-client" aria-hidden="true">#</a></h4><blockquote><p>CesiumJS 3D 虚拟地球的扩展</p></blockquote><ul><li>使用Importer/Exporter支持高效显示、动态加载和卸载从3DCityDB导出的平铺KML/glTF数据集形式的大型3D可视化模型</li><li>层管理，用于添加和删除任意数量的数据层，如三维可视化模型（KML/glTF）、3DTiles数据集、WMS图像层和Cesium数字地形模型。</li><li>三维可视化模型可以通过PostgREST与谷歌电子表格或数据库表格相连接，用于动态查询点击的三维对象的主题数据</li><li>与三维可视化模型进行丰富的互动，例如，在鼠标移动和鼠标点击时突出显示三维对象，以及隐藏选定的三维对象。</li><li>切换三维物体和地形的阴影投射</li><li>使用第三方服务，如微软必应地图的斜视图、谷歌街景和综合版本（DualMaps），从不同的视角探索选定的三维对象</li><li>使用场景链接保存和分享3DCityDB网络客户端项目（所有内容都存储在URL中）。</li></ul><h3 id="_6、demos" tabindex="-1">6、Demos <a class="header-anchor" href="#_6、demos" aria-hidden="true">#</a></h3><blockquote><p>有三个关于该数据库的Demo演示，不是重点，略</p></blockquote><ul><li><a href="https://www.3dcitydb.org/3dcitydb/visualizationberlin/" target="_blank" rel="noreferrer">Visualization of the 3D City Model of Berlin</a></li><li><a href="https://www.lrg.tum.de/gis/projekte/new-york-city-3d/" target="_blank" rel="noreferrer">Visualization of the 3D City Model of New York City</a></li><li><a href="https://www.3dcitydb.org/3dcitydb/visualizationsapp/" target="_blank" rel="noreferrer">Visualizations for Different Applications</a></li></ul><h3 id="_7、software" tabindex="-1">7、Software <a class="header-anchor" href="#_7、software" aria-hidden="true">#</a></h3><blockquote><p>有关3D City DB 的相关软件</p></blockquote><h4 id="_3d-city-database-1" tabindex="-1">3D City Database <a class="header-anchor" href="#_3d-city-database-1" aria-hidden="true">#</a></h4><ul><li>三维城市数据库是一个免费的三维地理数据库解决方案，可以在空间关系数据库系统Oracle Spatial/Locator或PostGIS的PostgreSQL之上存储、表示和管理虚拟三维城市模型。该数据库模型包含语义丰富、分层结构、多尺度的城市对象，有利于复杂的GIS建模和分析任务，远远超出了可视化的范围。三维城市数据库的模式是基于城市地理标记语言（CityGML），它是代表和交换虚拟三维城市模型的国际标准。</li><li>三维城市数据库以SQL脚本集的形式发货，允许在Oracle Spatial或PostgreSQL/PostGIS DBMS的基础上创建和删除三维城市数据库的实例。同时还提供了数据库模式建模软件的项目文件（Oracle：JDeveloper，PostgreSQL：pgModeler）。</li><li>3DCityDB支持Docker，也就是说，所有的软件模块都可以在Docker容器中运行。下载包括PostGIS/PostgreSQL在内的3DCityDB，并设置一个空的数据库，只需一个命令就可以完成！请看这里的说明！请看这里的说明。</li></ul><h4 id="importer-exporter" tabindex="-1">Importer/Exporter <a class="header-anchor" href="#importer-exporter" aria-hidden="true">#</a></h4><ul><li>三维城市数据库导入器/导出器是三维城市数据库的一个基于Java的前端。它允许高性能地导入和导出虚拟三维城市模型的空间数据。城市地理标记语言（CityGML）被用作数据交换格式，支持任意文件大小的CityGML实例文档。从4.3.0版本开始，CityJSON也被支持作为导入/导出格式。</li><li>三维城市数据库的内容可以用KML、COLLADA和glTF格式导出，为不同的需求创造单独的可视化。KML/COLLADA/glTF模型在广泛的应用中得到支持，如谷歌地球、Cesium、NASA World Wind或ArcGIS等地球浏览器。内置支持对象高亮和KML信息气球的通用创建，促进了对你的3D城市模型的互动探索。</li><li>三维城市数据库导入/导出器带有用于终端用户互动的图形用户界面（GUI）和命令行界面（CLI）。后者允许在批处理工作流程中使用该工具，或将其功能嵌入第三方应用程序中。</li></ul><h4 id="importer-exporter-plugins" tabindex="-1">Importer/Exporter Plugins <a class="header-anchor" href="#importer-exporter-plugins" aria-hidden="true">#</a></h4><ul><li>导入器/导出器插件 三维城市数据库导入/导出器的功能可以通过安装插件，以模块化和渐进的方式进行扩展，增加与三维城市数据库或外部数据互动的特定功能。用于开发的SDK包含在下载包中。</li><li>开放源代码 电子表格生成器插件允许将三维城市数据库实例中的特征属性作为电子表格进行通用输出。支持的输出格式是CSV和微软Excel的可变格式。</li><li>ADE管理器是三维城市数据库导入器/导出器（从4.0.0版本开始）的一个插件，允许用户和开发者动态扩展三维城市数据库（3DCityDB）实例，以便高性能地存储和管理CityGML应用域扩展（ADE）。</li><li>这些插件是Importer/Exporter软件包的一部分，可以在设置时选择安装。</li></ul><h4 id="web-feature-service" tabindex="-1">Web Feature Service <a class="header-anchor" href="#web-feature-service" aria-hidden="true">#</a></h4><ul><li>网络特征服务 三维城市数据库带有一个符合OGC标准的基本网络特征服务（WFS）的实现，允许基于网络访问存储在数据库中的三维城市对象。WFS客户可以直接连接到这个接口，并为各种不同的目的检索三维内容。WFS提供了一个标准化的、开放的接口，使用与平台无关的调用在网络上请求地理特征。WFS不是在文件层面上分享地理信息，而是在特征和属性层面上提供对地理信息的细粒度访问。网络特征服务允许客户只检索或修改他们所寻求的数据，而不是检索一个包含他们所寻求的数据以及可能更多的数据的文件。</li><li>三维城市数据库的WFS接口是根据OGC网络特征服务标准的最新2.0版本（OGC文件编号：09-025r2）实现的，因此符合ISO 19142:2010。该实现目前满足了简单WFS的一致性等级。开发伙伴virtualcitySYSTEMS可以提供完整的、事务性的WFS的实施方案，这在商业上是可行的。</li><li>3DCityDB支持Docker，也就是说，3DCityDB和WFS可以在Docker容器中运行。关于如何将WFS作为Docker容器运行的说明在这里给出。</li></ul><h4 id="_3dcitydb-web-map-client-1" tabindex="-1">3DCityDB-Web-Map-Client <a class="header-anchor" href="#_3dcitydb-web-map-client-1" aria-hidden="true">#</a></h4><ul><li>3DCityDB-Web-Map-Client是3DCityDB的前端，用于基于网络的3D可视化和任意大型语义3D城市模型的互动探索。3DCityDB-Web-Map-Client是基于Cesium Virtual Globe开发的，这是一个由Analytical Graphics, Inc.（AGI）开发的开源JavaScript库。它利用HTML5和Web图形库（WebGL）作为其硬件加速的核心，并提供跨平台的功能，如在网络上显示3D图形内容而不需要额外的插件。</li><li>在开发3DCityDB-Web-Map-Client的过程中，我们对Cesium Virtual Globe进行了各种扩展，以便于用户方便地查看和探索3D城市模型。其中最主要的扩展是，使用导入/导出工具导出的KML/glTF模型现在可以通过3DCityDB-Web-Map-Client在网络浏览器中与图像和地形层一起直接可视化，此外还可以将KML/glTF模型与使用电子表格生成器插件（SPSHG）导出的表格数据联系起来，并允许查询每个城市对象的专题数据。请看在线演示中的新功能。</li><li>3DCityDB-Web-Map-Client还配备了一个扩展，为移动设备提供更好的支持。该扩展带有一个内置的移动检测器，它可以自动检测并根据3DCityDB-Web-Map-Client是否在移动设备上运行而相应地调整客户端的行为。这个扩展所启用的一些最重要的移动功能以及如何使用它们，在该工具的GitHub页面上有描述。</li></ul><h4 id="citygml4j" tabindex="-1">citygml4j <a class="header-anchor" href="#citygml4j" aria-hidden="true">#</a></h4><ul><li>citygml4j是一个开源的Java类库和API，用于促进OGC城市地理标记语言（CityGML）的工作。citygml4j使读取、处理和编写CityGML数据集以及开发CityGML感知软件应用程序变得容易。</li><li>citygml4j将CityGML的XML Schema定义与一个定义明确的Java对象模型结合起来。CityGML实例文档被反序列化为一棵对应的Java对象，代表实例文档的内容和组织。这种XML绑定的方法允许Java开发人员将CityGML数据和处理功能纳入Java应用程序，而不需要了解XML或低级别的XML处理API。开发人员不必考虑解析XML数据，而是可以专注于业务逻辑并与定义良好的CityGML对象一起工作。在处理完CityGML数据后，Java内容树可以很容易地被序列化回CityGML实例文档中。</li></ul><blockquote><p>补充</p><ul><li>软件 和 文档均可在下载界面查看</li><li>对应GitHub 的官网地址： <a href="https://github.com/3dcitydb" target="_blank" rel="noreferrer">https://github.com/3dcitydb</a></li><li>Docker映像 ： <a href="https://3dcitydb-docs.readthedocs.io/en/latest/first-steps/docker.html" target="_blank" rel="noreferrer">https://3dcitydb-docs.readthedocs.io/en/latest/first-steps/docker.html</a></li></ul></blockquote><h3 id="_8、downloads" tabindex="-1">8、Downloads <a class="header-anchor" href="#_8、downloads" aria-hidden="true">#</a></h3><blockquote><ul><li>通过 <code>通用安装包</code>可以一键式安装全部软件</li><li>也可通过左侧的下载栏单独下载需要的内容</li></ul></blockquote><ul><li>本次安装4.3.0版本，以后所有内容均为对应笔记。</li></ul><p><img src="'+l+'" alt="image-20230624101250114"></p><ul><li><p>对应在线官网文档链接： <a href="https://3dcitydb-docs.readthedocs.io/en/latest/" target="_blank" rel="noreferrer">https://3dcitydb-docs.readthedocs.io/en/latest/</a></p></li><li><p>4.3.0版本对应以下子软件版本</p><table><thead><tr><th><strong>Component</strong></th><th><strong>Version</strong></th></tr></thead><tbody><tr><td><a href="https://3dcitydb-docs.readthedocs.io/en/latest/3dcitydb/index.html#dcitydb-chapter" target="_blank" rel="noreferrer">3D City Database</a></td><td>4.4</td></tr><tr><td><a href="https://3dcitydb-docs.readthedocs.io/en/latest/impexp/index.html#impexp-chapter" target="_blank" rel="noreferrer">Importer/Exporter</a><a href="https://3dcitydb-docs.readthedocs.io/en/latest/plugins/spreadsheet/index.html#impexp-plugin-spshg-chapter" target="_blank" rel="noreferrer">Spreadsheet Generator Plugin</a><a href="https://3dcitydb-docs.readthedocs.io/en/latest/plugins/ade-manager/index.html#impexp-plugin-ade-manager-chapter" target="_blank" rel="noreferrer">ADE Manager Plugin</a></td><td>5.34.22.2</td></tr><tr><td><a href="https://3dcitydb-docs.readthedocs.io/en/latest/webmap/index.html#webmap-chapter" target="_blank" rel="noreferrer">3D Web Map Client</a></td><td>1.9</td></tr><tr><td><a href="https://3dcitydb-docs.readthedocs.io/en/latest/wfs/index.html#wfs-chapter" target="_blank" rel="noreferrer">Web Feature Service</a></td><td>5.3</td></tr></tbody></table></li><li><p>安装步骤</p><p><img src="'+r+'" alt="image-20230624102223267"></p><p><img src="'+d+'" alt="image-20230624102235372"></p><p><img src="'+o+'" alt="image-20230624102308724"></p><p><img src="'+s+'" alt="image-20230624102327052"></p><p><img src="'+c+'" alt="image-20230624102349610"></p><p><img src="'+n+'" alt="image-20230624102518208"></p><p><img src="'+p+'" alt="image-20230624102543315"></p><p><img src="'+h+'" alt="image-20230624102552947"></p><p><img src="'+b+'" alt="image-20230624102618610"></p><p><img src="'+u+'" alt="image-20230624102654597"></p><p><img src="'+g+'" alt="image-20230624102816890"></p></li></ul><h3 id="_9、partners" tabindex="-1">9、Partners <a class="header-anchor" href="#_9、partners" aria-hidden="true">#</a></h3><blockquote><p>目前CityDB已经加入的伙伴，不是重点，略</p></blockquote><h3 id="_10、3dcitydb-in-action" tabindex="-1">10、3DCityDB in Action <a class="header-anchor" href="#_10、3dcitydb-in-action" aria-hidden="true">#</a></h3><blockquote><p>介绍应用案例的使用情况，如德国柏林的使用情况，不是重点，略</p></blockquote><h3 id="_11、publications" tabindex="-1">11、Publications <a class="header-anchor" href="#_11、publications" aria-hidden="true">#</a></h3><blockquote><p>关于3DCityDB相关的出版物 或者 文章，不是重点，略。</p></blockquote>',59),y=[_];function D(f,C,k,x,S,w){return i(),t("div",null,y)}const v=e(m,[["render",D]]);export{L as __pageData,v as default};
