import{_ as l,c as i,o as e,a}from"./app.8f8be3df.js";const f=JSON.parse('{"title":"CorelDraw --笔记","description":"","frontmatter":{},"headers":[{"level":3,"title":"一、界面","slug":"一、界面","link":"#一、界面","children":[]},{"level":3,"title":"二、基础操作","slug":"二、基础操作","link":"#二、基础操作","children":[{"level":4,"title":"界面操作","slug":"界面操作","link":"#界面操作","children":[]},{"level":4,"title":"对象选择","slug":"对象选择","link":"#对象选择","children":[]},{"level":4,"title":"其他操作","slug":"其他操作","link":"#其他操作","children":[]}]},{"level":3,"title":"三、文本工具","slug":"三、文本工具","link":"#三、文本工具","children":[]},{"level":3,"title":"四、制表符","slug":"四、制表符","link":"#四、制表符","children":[]},{"level":3,"title":"五、绘图工具","slug":"五、绘图工具","link":"#五、绘图工具","children":[]},{"level":3,"title":"六、矩形工具","slug":"六、矩形工具","link":"#六、矩形工具","children":[]},{"level":3,"title":"七、裁剪工具组","slug":"七、裁剪工具组","link":"#七、裁剪工具组","children":[]},{"level":3,"title":"八、对象编辑","slug":"八、对象编辑","link":"#八、对象编辑","children":[]},{"level":3,"title":"九、阴影工具","slug":"九、阴影工具","link":"#九、阴影工具","children":[]},{"level":3,"title":"十、度量工具","slug":"十、度量工具","link":"#十、度量工具","children":[]}],"relativePath":"软件/CorelDraw.md","lastUpdated":1667130584000}'),r={name:"软件/CorelDraw.md"},u=a('<h1 id="coreldraw-笔记" tabindex="-1">CorelDraw --笔记 <a class="header-anchor" href="#coreldraw-笔记" aria-hidden="true">#</a></h1><h3 id="一、界面" tabindex="-1">一、界面 <a class="header-anchor" href="#一、界面" aria-hidden="true">#</a></h3><ul><li>菜单栏</li><li>功能栏</li><li>工具栏</li><li>工具组 <ul><li>不同工具的属性栏</li></ul></li><li>文件标题栏 <ul><li>一个文件中可有多页</li></ul></li><li>工作区</li><li>状态栏</li></ul><h3 id="二、基础操作" tabindex="-1">二、基础操作 <a class="header-anchor" href="#二、基础操作" aria-hidden="true">#</a></h3><h4 id="界面操作" tabindex="-1">界面操作 <a class="header-anchor" href="#界面操作" aria-hidden="true">#</a></h4><ul><li>放大缩小 <ul><li>shift按键可以缩小</li><li>滚轮键向前放大</li><li>滚轮键向后缩小</li></ul></li><li>平移 <ul><li>按住滚轮键</li></ul></li><li>颜色 <ul><li>填充颜色左键填充，右键是边框颜色更改</li></ul></li></ul><h4 id="对象选择" tabindex="-1">对象选择 <a class="header-anchor" href="#对象选择" aria-hidden="true">#</a></h4><ul><li><p>取消所有对象组合</p></li><li><p>手绘选择</p><ul><li>alt键再选中对象的一点就可选中全部</li></ul></li><li><p>形状工具</p><ul><li>锚点更改位置</li></ul></li></ul><h4 id="其他操作" tabindex="-1">其他操作 <a class="header-anchor" href="#其他操作" aria-hidden="true">#</a></h4><ul><li>复制 ctrl C/V</li><li>填充 <ul><li>鼠标左键右侧的调色板</li><li>鼠标右键右侧的调色板 <ul><li>下方状态栏有更为详尽的颜色选择方案</li></ul></li></ul></li></ul><h3 id="三、文本工具" tabindex="-1">三、文本工具 <a class="header-anchor" href="#三、文本工具" aria-hidden="true">#</a></h3><ul><li>工具组中的字 <ul><li>直接单击：美术字 <ul><li>工具属性栏 <ul><li>XY : 所在位置</li><li>放大缩小字体</li></ul></li><li>单击中间十字架——切换旋转和放大缩小</li><li>水平 / 竖直 翻转</li><li>美术字 和 段落文本的对齐方式</li><li>文本属性 <ul><li>内部有多项属性功能</li></ul></li><li>其他：文本转曲，可以实现矢栅之间转换</li></ul></li><li>拉选框：段落文本 <ul><li>工具属性栏</li><li>文本属性</li></ul></li></ul></li><li>工具组中地路径文本 <ul><li>画好路径后--文本选中对应周边轮廓即可</li></ul></li></ul><h3 id="四、制表符" tabindex="-1">四、制表符 <a class="header-anchor" href="#四、制表符" aria-hidden="true">#</a></h3><ul><li>可单行 单列 多行 多列更改大小</li><li>制表符可通过调整水平、垂直来设置居中</li><li>制表符的外边框、内边框或者单一行等可调整边框粗细</li><li>通过顶部菜单栏 --表格，可将文字转换为表格</li></ul><h3 id="五、绘图工具" tabindex="-1">五、绘图工具 <a class="header-anchor" href="#五、绘图工具" aria-hidden="true">#</a></h3><ul><li>手绘工具可调整线宽、线形状等</li><li>两点线可绘制对应切线、垂直线</li><li>三点曲线、贝塞尔曲线等</li><li>钢笔工具，按住后移动可调整曲度 + 形状工具，可以调整节点所在位置及弯曲度</li></ul><p>补：转换为曲线后就可以对节点进行更改设置</p><h3 id="六、矩形工具" tabindex="-1">六、矩形工具 <a class="header-anchor" href="#六、矩形工具" aria-hidden="true">#</a></h3><ul><li>随便画出矩形</li><li>如果需要正方形，需要按住Ctrl</li><li>椭圆，如果需要画标准圆，按住Ctrl</li><li>其他形状工具</li></ul><h3 id="七、裁剪工具组" tabindex="-1">七、裁剪工具组 <a class="header-anchor" href="#七、裁剪工具组" aria-hidden="true">#</a></h3><ul><li><p>裁剪工具，可裁剪出需要的图片区域。</p></li><li><p>刻刀工具，可将图片裁剪成两部分，分别用</p></li><li><p>虚拟段删除，可以删除内部的连接线</p><ul><li>内部删除完毕后，可以将外部轮廓线通过顶部的焊接工具将边框焊接为一个整体</li></ul></li><li><p>橡皮擦工具，可以对转曲后的图形擦除工作</p></li></ul><h3 id="八、对象编辑" tabindex="-1">八、对象编辑 <a class="header-anchor" href="#八、对象编辑" aria-hidden="true">#</a></h3><ul><li>泊坞窗 <ul><li>变换：位置、旋转、缩放和镜像、大小和倾斜 <ul><li>注意副本的数目是否为0</li></ul></li><li>对齐与分布 <ul><li>用于调整对象之间的对齐方式和中间的间隔</li></ul></li></ul></li><li>对象造型工具（同ArcMap空间分析工具箱） <ul><li>焊接</li><li>修建</li><li>相交</li><li>移除前方/后方对象</li><li>创建边界</li></ul></li></ul><h3 id="九、阴影工具" tabindex="-1">九、阴影工具 <a class="header-anchor" href="#九、阴影工具" aria-hidden="true">#</a></h3><ul><li>阴影：可调整阴影的方向、程度以及羽化程度（模糊程度）</li><li>轮廓图 <ul><li>向内或向外增加轮廓</li><li>更改轮廓数目、各轮廓间的宽度、顶角样式等</li></ul></li><li>混合 <ul><li>显示出两个图形之间的相互转化过程</li></ul></li><li>变形和封套，可作出很多不规则图形，如旋转手里剑</li><li>立体化 <ul><li>可将某一几何对象改为立体图形</li></ul></li><li>块阴影 <ul><li>可朝某个方向拉出一片阴影区域</li></ul></li></ul><h3 id="十、度量工具" tabindex="-1">十、度量工具 <a class="header-anchor" href="#十、度量工具" aria-hidden="true">#</a></h3><ul><li>水平、垂直或倾斜度量</li><li>线段度量</li><li>其他标注</li><li>锚点工具</li></ul>',27),h=[u];function d(t,n,c,s,o,p){return e(),i("div",null,h)}const x=l(r,[["render",d]]);export{f as __pageData,x as default};
